import { useEffect } from 'react';
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas';
import { useSelector, useDispatch } from 'react-redux';
import { setYAxis } from '../../redux/slices/yAxis'; // Import the action creator
import './styles.css';

function Hero() {
    const yAxis = useSelector(state => state.yAxis.yAxis);
    const dispatch = useDispatch();

    const scrollAnimate = () => {
        let increasing = true;
        let currentValue = 0;

        const animate = () => {
            if (increasing) {
                currentValue += 1;
                if (currentValue >= 30) {
                    increasing = false;
                }
            } else {
                currentValue = 0;
                if (currentValue <= 0) {
                    increasing = true;
                }
            }

            dispatch(setYAxis(currentValue)); // Update the yAxis value in Redux

            setTimeout(() => {
                requestAnimationFrame(animate);
            }, 50); // Adjust the delay time (in milliseconds) for a slower animation
        };

        animate();
    };



    useEffect(() => {
        scrollAnimate();
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            <div className='cover'>
                <h4>Christian Hernandez</h4>
                <h1>Website and UX/UI Designer</h1>
                <p>A web developer looking to create amazing art for the web.</p>
            </div>
            <div className='scroll-behaviour'>
                <span className='dot' style={{ top: yAxis }}></span>
            </div>
        </section>
    );
}

export default Hero;
