// redux/pTextAnimation.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    pTextAnimation: -120,
};

export const pTextAnimationSlice = createSlice({
    name: 'pTextAnimation',
    initialState,
    reducers: {
        setPTextAnimation: (state, action) => {
            state.pTextAnimation = action.payload;
        },
    },
});

export const { setPTextAnimation } = pTextAnimationSlice.actions;

export default pTextAnimationSlice.reducer;
