// redux/dotAnimation.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    dotAnimation: 0,
};

export const dotAnimationSlice = createSlice({
    name: 'dotAnimation',
    initialState,
    reducers: {
        setDotAnimation: (state, action) => {
            state.dotAnimation = action.payload;
        },
    },
});

export const { setDotAnimation } = dotAnimationSlice.actions;

export default dotAnimationSlice.reducer;
