import { useEffect } from 'react'
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas'
// import { useSelector, useDispatch } from 'react-redux'
import './styles.css'

function Hero() {
    const dotAnimation = useSelector(state => state.dotAnimation.dotAnimation);
    const dispatch = useDispatch()

    const scrollAnimate = () => {
        let y = 0

        if (y >= 40) {
            y = y + 1
        } else if (y == 40) {
            y = 0
        }

        console.log(y)
    }

    useEffect(() => {
        scrollAnimate()
    }, [])

    return (
        <section className='Hero'>
            <ParticleCanvas />
            <div className='cover'>
                <h4>Christian Hernandez</h4>
                <h1>Website and UX/UI Designer</h1>
                <p>A web developer looking to create amazing art for the web.</p>
            </div>
            <div className='scroll-behaviour'>
                <span className='dot'></span>
            </div>
        </section>
    )
}

export default Hero
