import React, { useRef, useEffect } from 'react';

const ParticleCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight * 0.5; // Set canvas height to 50% of screen height

        const particles = [];

        function Particle(x, y) {
            this.x = x;
            this.y = y;
            this.size = Math.random() * 5 + 2;
            this.speedX = Math.random() * 3 - 1.5;
            this.opacity = Math.random() * 0.5 + 0.5; // Initial opacity between 0.5 and 1

            this.update = () => {
                this.x += this.speedX;
                this.y -= 0.5;
                this.opacity -= 0.01; // Decrease opacity over time
            };

            this.draw = () => {
                ctx.fillStyle = `rgba(0, 0, 0, ${this.opacity})`; // Set color to black
                ctx.beginPath();
                ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                ctx.fill();
            };
        }

        function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            particles.forEach((particle, index) => {
                particle.update();
                particle.draw();

                if (particle.opacity <= 0) {
                    particles.splice(index, 1);
                }
            });

            requestAnimationFrame(animate);
        }

        const createParticles = () => {
            for (let i = 0; i < 20; i++) {
                const x = Math.random() * canvas.width;
                const y = Math.random() * canvas.height;
                particles.push(new Particle(x, y));
            }
        };

        createParticles();
        animate();

    }, []);

    return <canvas ref={canvasRef} />;
};

export default ParticleCanvas;
