import { useEffect } from 'react';
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas';
import { useSelector, useDispatch } from 'react-redux';
import { setYAxis } from '../../redux/slices/yAxis'; // Import the action creator
import { setIsOpacity } from '../../redux/slices/isOpacity';
import { setTextAnimate } from '../../redux/slices/textAnimate';
import { setTextOpacity } from '../../redux/slices/textOpacity';
import profileImg from '../../assets/download (2).png'
import './styles.css';

function Hero() {
    const yAxis = useSelector(state => state.yAxis.yAxis);
    const isOpacity = useSelector(state => state.isOpacity.isOpacity);
    const textAnimate = useSelector(state => state.textAnimate.textAnimate);
    const textOpacity = useSelector(state => state.textOpacity.textOpacity);
    const dispatch = useDispatch();

    const scrollAnimate = () => {
        let increasing = true;
        let currentValue = 0;
        let opacityValue = 1

        const animate = () => {
            if (increasing) {
                currentValue += 1;
                opacityValue -= .04
                if (currentValue >= 30) {
                    increasing = false;
                }
            } else {
                currentValue = 0;
                opacityValue = 1
                if (currentValue <= 0) {
                    increasing = true;
                }
            }

            dispatch(setYAxis(currentValue)); // Update the yAxis value in Redux
            dispatch(setIsOpacity(opacityValue))

            setTimeout(() => {
                requestAnimationFrame(animate);
            }, 25); // Adjust the delay time (in milliseconds) for a slower animation
        };

        animate();
    };

    const textAnimation = () => {
        let h4StartingPoint = -70;
        let h4Opacity = 0
        let increasingH4 = true;

        const textInterval = setInterval(() => {
            if (increasingH4) {
                h4StartingPoint += 1;
                h4Opacity += .01
            }

            if (h4StartingPoint >= 0) {
                increasingH4 = false;
            }

            dispatch(setTextAnimate({ h4: h4StartingPoint }));
            dispatch(setTextOpacity({ h4: h4Opacity }))
        },);

        return () => {
            clearInterval(textInterval); // Clear the interval when the component unmounts
        };
    };

    useEffect(() => {
        scrollAnimate();
        const textCleanup = textAnimation();

        return () => {
            textCleanup(); // Call the cleanup function returned from textAnimation
        };
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            <div className='parallax-bg'>
                <span className='circle-1'></span>
                <span className='circle-2'></span>
                <span className='circle-3'></span>
                <span className='circle-4'></span>
            </div>
            <div className='cover-wrapper'>
                <div className='cover'>
                    <h4 style={{ bottom: textAnimate.h4, opacity: textOpacity.h4 }}>Christian Hernandez</h4>
                    <h1>Website and <br />
                        UX/UI Designer
                    </h1>
                    <p>A web developer looking to create amazing art for the web.</p>
                </div>
                <img className='profile-img' src={profileImg} />
            </div>
            <div className='scroll-behaviour'>
                <span className='dot' style={{ top: yAxis, opacity: isOpacity }}></span>
            </div>
        </section>
    );
}

export default Hero;
