import React, { useRef, useEffect } from 'react';

const ParticleCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        updateCanvasDimensions(canvas, ctx); // Update canvas dimensions initially


        const particles = [];

        class Particle {
            constructor(x, y) {
                this.x = x;
                this.y = y;
                this.size = Math.random() * 5 + 2;
                this.speedX = Math.random() * .5 - .1;
                this.opacity = 0;
                this.targetOpacity = Math.random() * 0.4 + 0.1; // Random opacity target between 0.1 and 0.5

                this.update = () => {
                    this.x += this.speedX;
                    this.y -= 0.5;

                    if (this.x > canvas.width) {
                        this.x = 0;
                    } else if (this.x < 0) {
                        this.x = canvas.width;
                    }

                    if (this.y > canvas.height) {
                        this.y = 0;
                    } else if (this.y < 0) {
                        this.y = canvas.height;
                    }

                    if (this.opacity < this.targetOpacity) {
                        this.opacity += 0.005;
                    } else if (this.opacity > this.targetOpacity) {
                        this.opacity -= 0.005;
                    }
                };

                this.draw = () => {
                    ctx.fillStyle = `rgba(0, 0, 0, ${this.opacity})`;
                    ctx.beginPath();
                    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                    ctx.fill();
                };
            }
        }

        function updateCanvasDimensions(canvas, ctx) {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight * 0.5; // Set canvas height to 50% of screen height

            // Redraw particles with updated canvas dimensions
            particles.forEach((particle) => {
                particle.update();
                particle.draw();
            });
        }

        function handleResize() {
            updateCanvasDimensions(canvas, ctx);
        }

        window.addEventListener('resize', handleResize);

        function createParticles() {
            for (let i = 0; i < 10; i++) {
                const x = Math.random() * canvas.width;
                const y = Math.random() * canvas.height;
                particles.push(new Particle(x, y));
            }
        }

        function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            particles.forEach((particle) => {
                particle.update();
                particle.draw();
            });

            requestAnimationFrame(animate);
        }

        createParticles();
        animate();

    }, []);

    return <canvas ref={canvasRef} />;
};

export default ParticleCanvas;
