import React, { useRef, useEffect } from 'react';

const ParticleCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight * 0.5; // Set canvas height to 50% of screen height

        const particles = [];

        function Particle(x, y) {
            this.x = x;
            this.y = y;
            this.size = Math.random() * 5 + 2;
            this.speedX = Math.random() * 3 - 1.5;
            this.opacity = 0;

            this.update = () => {
                this.x += this.speedX;
                this.y -= 0.5;

                if (this.opacity < 1) {
                    this.opacity += 0.005; // Increase opacity gradually
                } else {
                    this.opacity -= 0.005; // Decrease opacity gradually when fading out
                    if (this.opacity <= 0) {
                        this.x = Math.random() * canvas.width; // Reset particle position
                        this.y = Math.random() * canvas.height; // Reset particle position
                    }
                }
            };

            this.draw = () => {
                ctx.fillStyle = `rgba(0, 0, 0, ${this.opacity})`;
                ctx.beginPath();
                ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                ctx.fill();
            };
        }

        function createParticles() {
            for (let i = 0; i < 20; i++) {
                const x = Math.random() * canvas.width;
                const y = Math.random() * canvas.height;
                particles.push(new Particle(x, y));
            }
        }

        function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            particles.forEach((particle) => {
                particle.update();
                particle.draw();
            });

            requestAnimationFrame(animate);
        }

        createParticles();
        animate();

    }, []);

    return <canvas ref={canvasRef} />;
};

export default ParticleCanvas;
