// redux/h1TextOpacity.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    h1TextOpacity: 1,
};

export const h1TextOpacitySlice = createSlice({
    name: 'h1TextOpacity',
    initialState,
    reducers: {
        setH1TextOpacity: (state, action) => {
            state.h1TextOpacity = action.payload;
        },
    },
});

export const { setH1TextOpacity } = h1TextOpacitySlice.actions;

export default h1TextOpacitySlice.reducer;
