import { useEffect } from 'react';
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas';
import { useSelector, useDispatch } from 'react-redux';
import { setDotAnimation } from '../../redux/slices/dotAnimation'; // Import the action creator
import './styles.css';

function Hero() {
    const dotAnimation = useSelector(state => state.dotAnimation.dotAnimation);
    const dispatch = useDispatch();

    const scrollAnimate = () => {
        const animate = () => {
            let updatedValue = dotAnimation + 1;
            if (updatedValue > 40) {
                updatedValue = 0;
            }
            dispatch(setDotAnimation(updatedValue)); // Update the dotAnimation value in Redux
            requestAnimationFrame(animate);
        };

        animate();
    };

    useEffect(() => {
        scrollAnimate();
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            <div className='cover'>
                <h4>Christian Hernandez</h4>
                <h1>Website and UX/UI Designer</h1>
                <p>A web developer looking to create amazing art for the web.</p>
            </div>
            <div className='scroll-behaviour'>
                <span className='dot' style={{ top: dotAnimation }}></span>
            </div>
        </section>
    );
}

export default Hero;
