// redux/h4TextAnimation.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    h4TextAnimation: -70,
};

export const h4TextAnimationSlice = createSlice({
    name: 'h4TextAnimation',
    initialState,
    reducers: {
        setH4TextAnimation: (state, action) => {
            state.h4TextAnimation = action.payload;
        },
    },
});

export const { setH4TextAnimation } = h4TextAnimationSlice.actions;

export default h4TextAnimationSlice.reducer;
