import { useEffect } from 'react';
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas';
import { useSelector, useDispatch } from 'react-redux';
import { setDotAnimation } from '../../redux/slices/dotAnimation'; // Import the action creator
import './styles.css';

function Hero() {
    const dotAnimation = useSelector(state => state.dotAnimation.dotAnimation);
    const dispatch = useDispatch();

    const scrollAnimate = () => {
        const animate = () => {
            // Increment dotAnimation using the action creator and dispatch
            const newDotAnimation = (dotAnimation + 1) % 41; // Loop from 0 to 40
            dispatch(setDotAnimation({ y: newDotAnimation }));

            // Log the dotAnimation value to the console
            console.log(newDotAnimation);

            // Continue animation
            requestAnimationFrame(animate);
        };

        // Start the animation
        animate();
    };

    useEffect(() => {
        scrollAnimate();
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            {/* Rest of your JSX */}
        </section>
    );
}

export default Hero;
