// redux/h1TextAnimation.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    h1TextAnimation: -90,
};

export const h1TextAnimationSlice = createSlice({
    name: 'h1TextAnimation',
    initialState,
    reducers: {
        setH1TextAnimation: (state, action) => {
            state.h1TextAnimation = action.payload;
        },
    },
});

export const { setH1TextAnimation } = h1TextAnimationSlice.actions;

export default h1TextAnimationSlice.reducer;
