import { useEffect } from 'react';
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas';
import { useSelector, useDispatch } from 'react-redux';
import { setYAxis } from '../../redux/slices/yAxis'; // Import the action creator
import { setIsOpacity } from '../../redux/slices/isOpacity';
import { setH4TextAnimate } from '../../redux/slices/h4TextAnimate';
import { setTextOpacity } from '../../redux/slices/textOpacity';
import profileImg from '../../assets/download (2).png'
import './styles.css';

function Hero() {
    const yAxis = useSelector(state => state.yAxis.yAxis);
    const isOpacity = useSelector(state => state.isOpacity.isOpacity);
    const h4TextAnimate = useSelector(state => state.h4TextAnimate.h4TextAnimate);
    const textOpacity = useSelector(state => state.textOpacity.textOpacity);
    const dispatch = useDispatch();

    const scrollAnimate = () => {
        let increasing = true;
        let currentValue = 0;
        let opacityValue = 1

        const animate = () => {
            if (increasing) {
                currentValue += 1;
                opacityValue -= .04
                if (currentValue >= 30) {
                    increasing = false;
                }
            } else {
                currentValue = 0;
                opacityValue = 1
                if (currentValue <= 0) {
                    increasing = true;
                }
            }

            dispatch(setYAxis(currentValue)); // Update the yAxis value in Redux
            dispatch(setIsOpacity(opacityValue))

            setTimeout(() => {
                requestAnimationFrame(animate);
            }, 25); // Adjust the delay time (in milliseconds) for a slower animation
        };

        animate();
    };

    const textAnimation = () => {
        let startingPoint = {
            h4StartingPoint: -70,
            h1StartingPoint: -90,
        };
        let textOpacity = {
            h4Opacity: 0,
            h1Opacity: 0,
        };
        let increasing = {
            increasingH4: true,
            increasingH1: true,
        };

        const textIntervalh4 = setInterval(() => {
            if (increasing.increasingH4) {
                startingPoint.h4StartingPoint += 1;
                textOpacity.h4Opacity += 0.01;
            }

            if (startingPoint.h4StartingPoint >= 0) {
                increasing.increasingH4 = false;
            }

            dispatch(setH4TextAnimate({ h4: startingPoint.h4StartingPoint }));
            dispatch(setTextOpacity({ h4: textOpacity.h4Opacity })); // You can dispatch opacity changes as well
        }, 10);

        const textIntervalh1 = setInterval(() => {
            if (increasing.increasingH1) {
                startingPoint.h1StartingPoint += 1;
                textOpacity.h1Opacity += 0.01;
            }

            if (startingPoint.h1StartingPoint >= 0) {
                increasing.increasingH1 = false;
            }
        }, 10);

        return () => {
            clearInterval(textIntervalh4);
            clearInterval(textIntervalh1);
        };
    };

    useEffect(() => {
        scrollAnimate();
        const textCleanup = textAnimation();

        return () => {
            textCleanup(); // Call the cleanup function returned from textAnimation
        };
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            <div className='parallax-bg'>
                <span className='circle-1'></span>
                <span className='circle-2'></span>
                <span className='circle-3'></span>
                <span className='circle-4'></span>
            </div>
            <div className='cover-wrapper'>
                <div className='cover'>
                    <h4 style={{ bottom: h4TextAnimate.h4, opacity: textOpacity.h4 }}>Christian Hernandez</h4>
                    <h1>Website and <br />
                        UX/UI Designer
                    </h1>
                    <p>A web developer looking to create amazing art for the web.</p>
                </div>
                <img className='profile-img' src={profileImg} />
            </div>
            <div className='scroll-behaviour'>
                <span className='dot' style={{ top: yAxis, opacity: isOpacity }}></span>
            </div>
        </section>
    );
}

export default Hero;
