// redux/pTextOpacity.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    pTextOpacity: 1,
};

export const pTextOpacitySlice = createSlice({
    name: 'pTextOpacity',
    initialState,
    reducers: {
        setPTextOpacity: (state, action) => {
            state.pTextOpacity = action.payload;
        },
    },
});

export const { setPTextOpacity } = pTextOpacitySlice.actions;

export default pTextOpacitySlice.reducer;
