// redux/h1TextAnimate.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    h1TextAnimate: -90,
};

export const h1TextAnimateSlice = createSlice({
    name: 'h1TextAnimate',
    initialState,
    reducers: {
        setH1TextAnimate: (state, action) => {
            state.h1TextAnimate = action.payload;
        },
    },
});

export const { setH1TextAnimate } = h1TextAnimateSlice.actions;

export default h1TextAnimateSlice.reducer;
