import { useEffect } from 'react';
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas';
import { useSelector, useDispatch } from 'react-redux';
import { setDotAnimation } from '../../redux/slices/dotAnimation'; // Import the action creator
import './styles.css';

function Hero() {
    const dotAnimation = useSelector(state => state.dotAnimation.dotAnimation);
    const dispatch = useDispatch();

    const scrollAnimate = () => {
        const animate = () => {
            // Increment dotAnimation using the action creator and dispatch
            if (dotAnimation >= 40) {
                dispatch(setDotAnimation({ y: 0 }));
            } else {
                dispatch(setDotAnimation({ y: dotAnimation + 1 }));
            }

            // Continue animation as long as dotAnimation is less than 40
            if (dotAnimation < 40) {
                requestAnimationFrame(animate);
            }
        };

        // Start the animation
        animate();
        console.log(dotAnimation)
    };

    useEffect(() => {
        scrollAnimate();
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            {/* Rest of your JSX */}
        </section>
    );
}

export default Hero;
