// redux/h4TextOpacity.js.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    h4TextOpacity: {},
};

export const h4TextOpacitySlice = createSlice({
    name: 'h4TextOpacity.js',
    initialState,
    reducers: {
        setH4TextOpacity: (state, action) => {
            state.h4TextOpacity.js = action.payload;
        },
    },
});

export const { setH4TextOpacity } = h4TextOpacitySlice.actions;

export default h4TextOpacitySlice.reducer;
