import { useEffect } from 'react'
import ParticleCanvas from '../ParticleCanvas/ParticleCanvas'
import { useSelector, useDispatch } from 'react-redux'
import './styles.css'

function Hero() {
    const dotAnimation = useSelector(state => state.dotAnimation.dotAnimation);
    const dispatch = useDispatch()

    const scrollAnimate = () => {
        const animate = () => {
            // Calculate the next dotAnimation value (looping from 0 to 40)
            const newDotAnimation = (dotAnimation + 1) % 41;

            // Dispatch the action to update dotAnimation in Redux store
            dispatch(setDotAnimation(newDotAnimation));

            // Log the updated dotAnimation value to the console
            console.log(newDotAnimation);

            // Continue animation
            requestAnimationFrame(animate);
        };

        // Start the animation
        animate();
    };

    useEffect(() => {
        scrollAnimate();
    }, []);

    return (
        <section className='Hero'>
            <ParticleCanvas />
            <div className='cover'>
                <h4>Christian Hernandez</h4>
                <h1>Website and UX/UI Designer</h1>
                <p>A web developer looking to create amazing art for the web.</p>
            </div>
            <div className='scroll-behaviour'>
                <span className='dot'></span>
            </div>
        </section>
    )
}

export default Hero
