// redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import yAxisReducer from './slices/yAxis';
import isOpacityReducer from './slices/isOpacity';
import h4TextAnimationReducer from './slices/h4TextAnimation';
import h1TextAnimationReducer from './slices/h1TextAnimation';
import h4TextOpacityReducer from './slices/h4TextOpacity';
import h1TextOpacityReducer from './slices/h1TextOpacity';

export const store = configureStore({
    reducer: {
        yAxis: yAxisReducer,
        isOpacity: isOpacityReducer,
        h4TextAnimation: h4TextAnimationReducer,
        h1TextAnimation: h1TextAnimationReducer,
        h4TextOpacity: h4TextOpacityReducer,
        h1TextOpacity: h1TextOpacityReducer
    }
});
